import React, { useState, useEffect, useRef } from "react";
import useAxios from "axios-hooks";
import {Result} from "../index"
import { Row, Col, Form } from "react-bootstrap";

export default ({location}) => {
  const [lang, setLang] = useState(null);
  const [isSubmit, setIsSubmit] = useState(false);
  const InputRef = useRef(null);

  if(!lang)
    return (
      <Form>
        <fieldset>
          <Form.Group as={Row}>
            <Form.Label className="w-100">
              میخواهید از چه زبانی ترجمه کنید؟
            </Form.Label>
            <Col sm={10}>
              <Form.Check
                value={lang === 'persian'}
                onClick={() => setLang('fa')}
                type="radio"
                label="از فارسی"
                name="formHorizontalRadios"
                id="from-persian"
              />
              <Form.Check
                value={lang === 'english'}
                onClick={() => setLang('en')}
                type="radio"
                id={"from-english"}
                label={`از انگلیسی`}
                name="formHorizontalRadios"
              />
            </Col>
          </Form.Group>
        </fieldset>
      </Form>
    )

  return (
    <>
      <Row className="row mt-3">
      <Col xs={12} sm={8} className="p-3">
        <label htmlFor="translate-from-input" className="float-right">
          {lang === "fa" ? "فارسی" : "انگلیسی"}
        </label>
        <input
          type="text"
          ref={InputRef}
          id="translate-form-input"
          name="translate-form-input"
          placeholder="کلمه خود را جستجو کنید"
          className="w-100"
          style={{ height: "40px" }}
        />
      </Col>

      <Col xs={12} sm={2} className="p-3">
        <button
          onClick={() => setIsSubmit(true)}
          className="btn btn-primary mx-auto my-4"
        >
          ترجمه کن
        </button>
      </Col>

      <Col xs={12} sm={2} className="p-3">
        <button
          onClick={() => {setLang(null); setIsSubmit(false)}}
          className="btn btn-primary mx-auto my-4"
        >
          ترجمه کلمه جدید
        </button>
      </Col>
    </Row>
    {isSubmit && <Result word={InputRef.current.value} lang={lang} />}
    </>
  );
};
