import React, { useEffect, useRef } from "react";
import useAxios from "axios-hooks";
import { Row, Col } from "react-bootstrap";

export default () => {
  const PersianInputRef = useRef(null);
  const EnglishInputRef = useRef(null);

  const [
    {
      data: dataFromPersian,
      loading: loadingFromPersian,
      error: errorFromPersian
    },
    refetchFromPersian
  ] = useAxios({
    url: `/translation/get?word=${PersianInputRef}&lang=fa`,
    method: 'get'
  }, {
    manual: true,
  });

  useEffect(() => {
    EnglishInputRef.current.value = dataFromPersian;
  }, [dataFromPersian])


  return (
    <Row class="row mt-3">
      <Col xs={12} sm={6} className="p-3">
        <label for="translate-from-input" class="float-right">
          فارسی
        </label>
        <input
          type="text"
          ref={PersianInputRef}
          id="translate-from-input"
          name="translate-from-input"
          placeholder="کلمه خود را جستجو کنید"
          class="w-100"
          style={{ height: "40px" }}
        />
      </Col>
      <Col xs={12} sm={6} className="p-3">
        <label for="translate-to-input" class="float-right">
          English
        </label>
        <input
          type="text"
          ref={EnglishInputRef}
          id="translate-to-input"
          name="translate-to-input"
          placeholder="معنای کلمه خود را بیابید"
          class="w-100"
          style={{ height: "40px" }}
        />
      </Col>

      <button
        onClick={() => handleTranslate()}
        class="btn btn-primary mx-auto my-4"
      >
        ترجمه کن
      </button>
    </Row>
  );
};
