import React, { useState, useEffect, useRef } from "react";
import useAxios from "axios-hooks";
import { Result } from "../index";
import { Row, Col, Form } from "react-bootstrap";

export default ({ location }) => {
  const [word, setWord] = useState("");
  const [lang, setLang] = useState("");
  const [isSubmit, setIsSubmit] = useState(false);

  if (!lang)
    return (
      <Row>
        <h2 className="text-center w-100">به تحریر خوش آمدید!</h2>

        <p>
          ما یه سامانه آنلاینیم که توی اون میتونی ترجمه های مختلف کلمه مد نظرت رو سرچ کنی، به ترجمه های جالب بقیه نمره بدی و نظر خودت رو با بقیه به اشتراک بزاری، قراره کلی رشد کنیم، جزو اولین نفر ها باش...
        </p>

        <Col>
          <div>پارسی</div>

          <Form.Check
            value={lang === "persian"}
            onClick={() => setLang("fa")}
            type="radio"
            label="از فارسی"
            name="formHorizontalRadios"
            id="from-persian"
          />
        </Col>

        <Col>
          <div>English</div>

          <Form.Check
            value={lang === "english"}
            onClick={() => setLang("en")}
            type="radio"
            id={"from-english"}
            label={`از انگلیسی`}
            name="formHorizontalRadios"
          />
        </Col>
      </Row>
    );

  return (
    <>
      <Row className="row mt-3">
        <Col xs={12} sm={8} className="p-3">
          <label htmlFor="translate-from-input" className="float-right">
            {lang === "fa" ? "فارسی" : "انگلیسی"}
          </label>
          <input
            type="text"
            value={word}
            onChange={e => {
              setWord(e.target.value);
              setIsSubmit(false);
            }}
            id="translate-form-input"
            name="translate-form-input"
            placeholder="کلمه خود را جستجو کنید"
            className="w-100"
            style={{ height: "40px" }}
          />
        </Col>

        <Col xs={12} sm={2} className="p-3">
          <button
            onClick={() => setIsSubmit(true)}
            className="btn btn-primary mx-auto my-4"
            disabled={isSubmit}
          >
            ترجمه کن
          </button>
        </Col>

        <Col xs={12} sm={2} className="p-3">
          <button
            onClick={() => {
              setLang(null);
              setIsSubmit(false);
              setWord("");
            }}
            className="btn btn-primary mx-auto my-4"
          >
            ترجمه کلمه جدید
          </button>
        </Col>
      </Row>

      {isSubmit && <Result word={word} lang={lang} />}
    </>
  );
};
