import React, {useState} from "react";
import useAxios from "axios-hooks";

import {SelectedResult, Loading} from "../index"

import { Row, Col } from "react-bootstrap";

export default props => {
  const [selectedResult, setSelectedResult] = useState({});
  const [{ data, loading, error }, refetchFromPersian] = useAxios({
    url: `/translation/get?word=${props.word}&lang=${props.lang}`,
    method: "get"
  });

  if(loading) return <Loading />

  console.log(data)

  return (
    <>
      <Row>
        {data.translations.map(item => {
          return (
            <Col key={item.translation} onClick={() => setSelectedResult(item)}>
              {item.translation}
            </Col>
          )
        })}
      </Row>
      {error && error.status === 404 ? (
        <div>
          <span>ترجمه کلمه وارد شده موجود نیست، از شما تقاضا داریم</span>
          <span>کلمه پیشنهاد دهید </span>
        </div>
      ) : null}
    {selectedResult.translation !== undefined ? (
      <SelectedResult selected={selectedResult} />
    ) : null}
    </>
  );
};
