import React, { useState, useEffect, useRef } from "react";
import useAxios from "axios-hooks";
import { Result } from "../index";
import { Row, Col, Form, Image } from "react-bootstrap";
import logoPNG from "assets/img/logo.png";

import "./style.scss";

export default ({ location }) => {
  const [word, setWord] = useState("");
  const [lang, setLang] = useState("");
  const [isSubmit, setIsSubmit] = useState(false);

  if (!lang)
    return (
      <Row className="pb-3">
        <div className="mx-auto p-3" style={{background: "aliceblue", borderRadius: "5rem"}}>
          <Image
            src={logoPNG}
            height={100}
            width={100}
            className="header-logo"
            alt="linkerpad"
          />
        </div>
        <h2 className="text-center w-100 my-5">به تحریر خوش آمدید!</h2>
        <div className="w-100 mb-5">
          <p className="mx-auto text-center" style={{maxWidth: "800px", margin: "2rem"}}>
            ما یه سامانه آنلاینیم که توی اون میتونی ترجمه های مختلف کلمه مد نظرت
            رو سرچ کنی، به ترجمه های جالب بقیه نمره بدی و نظر خودت رو با تحریریون
            به اشتراک بزاری، قراره کلی رشد کنیم، جزو اولین نفر ها باش...
          </p>
        </div>
        <Col>
          <div className="lang-btn" onClick={() => setLang("fa")}>
            <h3 style={{fontWeight: "400"}}>پارسی</h3>
          </div>
        </Col>

        <Col>
          <div className="lang-btn" onClick={() => setLang("en")}>
            <h3 style={{fontWeight: "400"}}>English</h3>
          </div>
        </Col>
      </Row>
    );

  return (
    <>
      <Row className="row mt-3">
        <Col xs={12} className="p-3 mr-auto">
          <label htmlFor="translate-from-input" className="float-right">
            {lang === "fa" ? "فارسی" : "انگلیسی"}
          </label>
          <input
            type="text"
            value={word}
            onChange={e => {
              setWord(e.target.value);
              setIsSubmit(false);
            }}
            id="translate-form-input"
            name="translate-form-input"
            placeholder="کلمه خود را جستجو کنید"
            className="w-100"
            style={{ height: "40px" }}
          />
        </Col>

        <Col xs={12} sm={8} md={6} lg={5} className="p-3 ml-auto">
          <button
            onClick={() => setIsSubmit(true)}
            className="btn btn-primary mx-auto my-4 w-100"
            disabled={isSubmit}
          >
            ترجمه کن
          </button>
        </Col>

        <Col xs={12} sm={8} md={6} lg={5} className="p-3">
          <button
            onClick={() => {
              setLang(null);
              setIsSubmit(false);
              setWord("");
            }}
            className="btn btn-primary mx-auto my-4"
          >
            ترجمه کلمه جدید
          </button>
        </Col>
      </Row>

      {isSubmit && <Result word={word} lang={lang} />}
    </>
  );
};
