import React, { useState, useEffect, useRef } from "react";
import useAxios from "axios-hooks";
import Result from "../index"
import { Row, Col, Form } from "react-bootstrap";

export default ({location}) => {
  const [lang, setLang] = useState(null);
  const [isSubmit, setIsSubmit] = useState(false);
  const InputRef = useRef(null);

  // const [
  //   {
  //     data: dataFromPersian,
  //     loading: loadingFromPersian,
  //     error: errorFromPersian
  //   },
  //   refetchFromPersian
  // ] = useAxios(
  //   {
  //     url: `/translation/get?word=${PersianInputRef.current.value}&lang=fa`,
  //     method: "get"
  //   },
  //   {
  //     manual: true
  //   }
  // );

  // useEffect(() => {
  //   if (dataFromPersian) EnglishInputRef.current.value = dataFromPersian;
  // }, [dataFromPersian]);

  if(!lang)
    return (
      <Form>
        <fieldset>
          <Form.Group as={Row}>
            <Form.Label className="w-100">
              میخواهید از چه زبانی ترجمه کنید؟
            </Form.Label>
            <Col sm={10}>
              <Form.Check
                value={lang === 'persian'}
                onClick={() => setLang('persian')}
                type="radio"
                label="از فارسی"
                name="formHorizontalRadios"
                id="from-persian"
              />
              <Form.Check
                value={lang === 'english'}
                onClick={() => setLang('english')}
                type="radio"
                id={"from-english"}
                label={`از انگلیسی`}
                name="formHorizontalRadios"
              />
            </Col>
          </Form.Group>
        </fieldset>
      </Form>
    )

  return (

    <Row class="row mt-3">
      <Col xs={12} sm={6} className="p-3">
        <label for="translate-from-input" class="float-right">
          {lang === "persian" ? "فارسی" : "انگلیسی"}
        </label>
        <input
          type="text"
          ref={InputRef}
          id="translate-form-input"
          name="translate-form-input"
          placeholder="کلمه خود را جستجو کنید"
          class="w-100"
          style={{ height: "40px" }}
        />
      </Col>

      <button
        onClick={() => setIsSubmit(true)}
        class="btn btn-primary mx-auto my-4"
      >
        ترجمه کن
      </button>

      {isSubmit && <Result word={InputRef.current.value} lang={lang} />}
    </Row>
  );
};
