import React from "react";
import { Link } from "react-router-dom";

import userSVG from "assets/img/user-logo.svg";

import { Nav, Navbar, NavDropdown, Image } from "react-bootstrap";
import "./style.scss"

export default ({ location }) => {
  return (
    <Navbar expand="lg" className="bg-light">
      <Navbar.Brand as={Link} to={"/profile"}>
        <Image
          src={"/assets/img/logo.png"}
          className="float-right header-logo"
          alt="linkerpad"
        />
      </Navbar.Brand>
      <Navbar.Toggle aria-controls="basic-navbar-nav" />
      <Navbar.Collapse className="d-flex" id="basic-navbar-nav">
        <Nav className="w-100 align-items-center px-3">
          <NavDropdown
            title={
              <>
                <Image
                  src={userSVG}
                  width={40}
                  className="float-right header-logo"
                  alt="user"
                />
                <span class="mr-2 vertical-align-middle">محمد هدشی</span>
              </>
            }
            className="mr-auto p-1 text-right"
          >
            <NavDropdown.Item as={Link} to={"/profile"}>
              حساب کاربری
            </NavDropdown.Item>
            <NavDropdown.Divider />
            <NavDropdown.Item as={Link} to={"/help"}>
              راهنما
            </NavDropdown.Item>
            <NavDropdown.Divider />
            <NavDropdown.Item as={Link} to={"/logout"}>
              خروج
            </NavDropdown.Item>
          </NavDropdown>
        </Nav>
      </Navbar.Collapse>
    </Navbar>
  );
};
