import React from "react"

export default props => {
  const [
    {
      data,
      loading,
      error,
    },
    refetchFromPersian
  ] = useAxios(
    {
      url: `/translation/get?word=${PersianInputRef.current.value}&lang=fa`,
      method: "get"
    },
    {
      manual: true
    }
  );

  useEffect(() => {
    if (dataFromPersian) EnglishInputRef.current.value = dataFromPersian;
  }, [dataFromPersian]);

  return (
    <div>result {props.lang} {props.word}</div>
  )
}
